"""Envoy admin listener endpoint port"""
ENVOY_ADMIN_PORT = 10000

"""The file extension used by envoy configuration files"""
FILE_EXT = "yml"

"""Envoy bootstrap file name"""
BOOTSTRAP_FILE_NAME = f"bootstrap.{FILE_EXT}"

"""Envoy cluster discovery service filename"""
CDS_FILE_NAME = f"cds.{FILE_EXT}"

"""Envoy Listener discovery service filename """
LDS_FILE_NAME = f"lds.{FILE_EXT}"

"""The base name used to represent envoy route discovery service file"""
RDS_BASE_FILE_NAME = "rds"

"""The base name used to represent envoy endpoint discovery service file"""
EDS_BASE_FILE_NAME = "eds"

"""The base name used to represent envoy secret discovery service file"""
SDS_BASE_FILE_NAME = "sds"

"""The loop back localhost ip address"""
LOOPBACK_IP_ADDRESS = "127.0.0.1"

"""The default ip address that listens on all interfaces"""
DEFAULT_IP_ADDRESS = "0.0.0.0"

"""This is the list tls cipher suites envoy would allow for tls connection"""
CIPHER_SUITES = [
    "ECDHE-ECDSA-AES128-GCM-SHA256",
    "ECDHE-RSA-AES128-GCM-SHA256",
    "ECDHE-ECDSA-AES256-GCM-SHA384",
    "ECDHE-RSA-AES256-GCM-SHA384",
    "AES256-GCM-SHA384",
    "AES128-GCM-SHA256",
]

"""This the prefix to which envoy will route traffic"""
ROOT_ROUTE_PREFIX = "/"

"""This is the cert file used by envoy to terminate incoming TLS connection"""
GENESYS_CERT_FILE = "/etc/genesys/certs/genesys_key.crt"

"""This the private key file used by envoy to terminate incoming TLS connection"""
GENESYS_PRIVATE_KEY_FILE = "/etc/genesys/certs/genesys_key.key"

"""The genesys tags.json file location"""
TAGS_JSON_FILE = "/etc/genesys/tags.json"
