"""
Exceptions for the static asset uploader
"""


class BaseUnifiedConfigException(Exception):
    """Base exception that others derive from"""


class ServiceNameValidationException(BaseUnifiedConfigException):
    """ Raised if there is a mismatch between clustername and servicename"""

class RepoPathOrMergedConfigValidationException(BaseUnifiedConfigException):
    """Raised if neither repo_path or merged_config is provided to Unified Config contrstuctor"""

class RepoPathOrMergedExclusiveConfigValidationException(BaseUnifiedConfigException):
    """Raised if neither repo_path or merged_config is provided to Unified Config contrstuctor"""

class NoConfigFoundException(BaseUnifiedConfigException):
    """Raised if we could not find any config files to load"""
