"""This module is responsible for exporting otel config"""
from typing import Dict

from .otel.constants import OTEL_COLLECTOR_LOG_DIR
from .otel.config import generate_open_telemetry_config


class OtelExport:
    """lightweight class that will return the otel config"""
    def __init__(self, unified_config: Dict, tags, collector_log_dir: str = OTEL_COLLECTOR_LOG_DIR):
        self.unified_config = unified_config
        self.tags = tags
        self.collector_log_dir = collector_log_dir

    def process(self):
        """Process request to create otel config"""
        return generate_open_telemetry_config(
            unified_config=self.unified_config,
            tags=self.tags,
            collector_log_dir=self.collector_log_dir)
