"""Item and Interface for biomes"""

from typing import Generator

from hyperion.items.base_item import Item, Interface


class BiomeInterface(Interface):
    """Interface for biomes"""

    @property
    def item_type(self) -> str:
        """The item type represented by this interface"""
        return 'biome'

    # pylint: disable=arguments-differ
    def iterate(
            self,
            expanded: bool = False,
            name_only: bool = False,
            ecosystem: str = None,
            environment: str = None,
            region: str = None
    ) -> Generator[Item, None, None]:
        """
        Iterates over biomes matching the provided filter(s)
        :param expanded: If True, iterates over expanded biomes
        :param name_only: If True, iterates over biomes which only contain the name
        :param ecosystem: The optional ecosystem filter
        :param environment: The optional environment filter
        :param region: The optional region filter
        :return: A generator that will iterate over and load/expand biomes
        """
        yield from self._list_all(
            expanded=expanded,
            name_only=name_only,
            ecosystem=ecosystem,
            environment=environment,
            region=region

        )
