"""Item and Interface for Ecosystems"""

from typing import Generator

from hyperion.items.base_item import Item, Interface


class EcosystemInterface(Interface):
    """Interface for ecosystems"""

    @property
    def item_type(self) -> str:
        """The item type represented by this interface"""
        return 'ecosystem'

    # pylint: disable=arguments-differ
    def iterate(
            self,
            expanded: bool = False,
            name_only: bool = False,
            display_name: str = None
    ) -> Generator[Item, None, None]:
        """
        Iterates over ecosystems matching the provided filter(s)
        :param expanded: If True, iterates over expanded ecosystems
        :param name_only: If True, iterates over ecosystems which only contain the name
        :param display_name: The optional displayName filter
        :return: A generator that will iterate over and load/expand ecosystems
        """
        yield from self._list_all(
            expanded=expanded,
            name_only=name_only,
            display_name=display_name
        )
