"""Item and Interface for Environments"""

from typing import Generator

from hyperion.items.base_item import Item, Interface


class EnvironmentInterface(Interface):
    """Interface for environments"""

    @property
    def item_type(self) -> str:
        """The item type represented by this interface"""
        return 'environment'

    # pylint: disable=arguments-differ
    def iterate(
            self,
            expanded: bool = False,
            name_only: bool = False,
            ecosystem: str = None,
            ou: str = None
    ) -> Generator[Item, None, None]:
        """
        Iterates over ecosystems matching the provided filter(s)
        :param expanded: If True, iterates over expanded environments
        :param name_only: If True, iterates over environments which only contain the name
        :param ecosystem: The optional ecosystem filter
        :param ou: The optional ou filter
        :return: A generator that will iterate over and load/expand environments
        """
        yield from self._list_all(
            expanded=expanded,
            name_only=name_only,
            ecosystem=ecosystem,
            ou=ou
        )
