"""Item and Interface for ous"""

from typing import Generator

from hyperion.items.base_item import Item, Interface


class OuInterface(Interface):
    """Interface for ous"""

    @property
    def item_type(self) -> str:
        """The item type represented by this interface"""
        return 'ou'

    # pylint: disable=arguments-differ
    def iterate(
            self,
            expanded: bool = False,
            name_only: bool = False,
            ou_id: str = None
    ) -> Generator[Item, None, None]:
        """
        Iterates over ous matching the provided filter(s)
        :param expanded: If True, iterates over expanded ous
        :param name_only: If True, iterates over ous which only contain the name
        :param ou_id: The optional ou ID filter
        :return: A generator that will iterate over and load/expand ous
        """
        yield from self._list_all(
            expanded=expanded,
            name_only=name_only,
            ou_id=ou_id
        )
