"""Item and Interface for Regions"""

from typing import Generator

from hyperion.items.base_item import Item, Interface


class RegionInterface(Interface):
    """Interface for regions"""

    @property
    def item_type(self) -> str:
        """The item type represented by this interface"""
        return 'region'

    # pylint: disable=arguments-differ
    def iterate(
            self,
            expanded: bool = False,
            name_only: bool = False,
            display_name: str = None,
            short_name: str = None
    ) -> Generator[Item, None, None]:
        """
        Iterates over regions matching the provided filter(s)
        :param expanded: If True, iterates over expanded regions
        :param name_only: If True, iterates over regions which only contain the name
        :param display_name: The optional displayName filter
        :param short_name: The optional shortName filter
        :return: A generator that will iterate over and load/expand regions
        """
        yield from self._list_all(
            expanded=expanded,
            name_only=name_only,
            display_name=display_name,
            short_name=short_name
        )
