"""Item and Interface for Sumos"""

from typing import Generator

from hyperion.items.base_item import Item, Interface


class SumoInterface(Interface):
    """Interface for sumos"""

    @property
    def item_type(self) -> str:
        """The item type represented by this interface"""
        return 'sumo'

    # pylint: disable=arguments-differ
    def iterate(
            self,
            expanded: bool = False,
            name_only: bool = False,
            account_id: str = None
    ) -> Generator[Item, None, None]:
        """
        Iterates over sumos matching the provided filter(s)
        :param expanded: If True, iterates over expanded sumos
        :param name_only: If True, iterates over sumos which only contain the name
        :param account_id: The optional accountId filter
        :return: A generator that will iterate over and load/expand sumos
        """
        yield from self._list_all(
            expanded=expanded,
            name_only=name_only,
            account_id=account_id
        )
